<?phpnamespace Base\Model;use Zend\InputFilter\InputFilterAwareInterface;use Zend\InputFilter\InputFilterInterface;abstract class BaseModel implements InputFilterAwareInterface {    protected $inputFilter;    protected $properties;        abstract public function exchangeArray( $data );    abstract public function getInputFilter( );    abstract public function addCustomFilters( &$inputFilter, $factory );    public function setInputFilter( InputFilterInterface $inputFilter ) {        $this->inputFilter = $inputFilter;    }        public function getPropertiesArray( ) {        return get_object_vars( $this );    }}