<?php// module/Application/src/Application/Form/User.php:namespace Dgx\Form;use Zend\Form\Form;use Zend\I18n\Translator\Translator;class UserForm extends Form {    protected $translator;    public function __construct( ) {        // we want to ignore the name passed        parent::__construct( 'User' );        $this->add( array(            'name' => 'id',            'attributes' => array(                'type' => 'hidden'             ),            'options' => array(                'label' => 'labels.user.id',                'helpText' => 'user.helpText.id',            )         ) );        $this->add( array(            'name' => 'firstname',            'attributes' => array(                'type' => 'text',                'size' => '50',            ),            'options' => array(                'label' => 'labels.user.firstname',                'helpText' => 'user.helpText.firstname',            )         ) );    }}?>