<?php// module/Application/src/Application/Model/UserTable.php:namespace Dgx\Model;use Zend\Db\TableGateway\TableGateway;use Base\Model\BaseTable;class UserTable extends BaseTable {    protected $table = 'user';/*     public function __construct( $adapter ) {        $this->setEntity( 'user' );        parent::__construct( $adapter );    } */       public function saveUser( User $User ) {        $data = array(            'artist' => $User->firstname,            'title' => $User->title         );        $id   = (int) $User->id;        if ( $id == 0 ) {            $this->tableGateway->insert( $data );        } else {            if ( $this->getUser( $id ) ) {                $this->tableGateway->update( $data, array(                     'id' => $id                 ) );            } else {                throw new \Exception( 'Form id does not exist' );            }        }    }        /*        public function fetchAll() {    $resultSet = $this->tableGateway->select();    return $resultSet;    }        public function deleteUser($id) {    $this->tableGateway->delete(array('id' => $id));    }    */}